random = new java.util.Random();

def holder = groovyUtils.getXmlHolder('GetSiteInfoObjectCall#Response');
holder.declareNamespace('ns1','http://www.cuahsi.org/his/1.0/ws/');
holder.declareNamespace('ns2','http://www.cuahsi.org/waterML/1.0/');

sites = holder.getNodeValues("//ns2:site" )
log.info("siteCount" + sites.length);
siteIndex = random.nextInt(sites.length);
if (siteIndex == 0 ) siteIndex = 1;
log.info("siteIndex " + siteIndex);
siteCode = holder.getNodeValue( String.format(resSiteCodeXpath,siteIndex,1) );
log.info("siteCode " + siteCode);
siteNetwork =  holder.getNodeValue(String.format(resSiteNetworkXpath,siteIndex,1));
log.info("siteNetwork " + siteNetwork);

resSeriesXpath = "//ns2:sitesResponse/ns2:site[%1d]/ns2:seriesCatalog[%2d]/ns2:series"; 
//resSeriesXpath = "//ns2:series";
series = holder.getNodeValues( String.format(resSeriesXpath,siteIndex, 1 ) );
log.info("seriesCount " + series.length);
seriesIndex = random.nextInt(series.length);
if (seriesIndex == 0 ) seriesIndex = 1;
log.info("SeriesIndex "+seriesIndex);

resVariableCodeXpath = "//ns2:sitesResponse/ns2:site[%1d]/ns2:seriesCatalog[%2d]/ns2:series[%3d]/ns2:variable//ns2:variableCode[%4d]";
resVariableVocabularyXpath = "//ns2:sitesResponse/ns2:site[%1d]/ns2:seriesCatalog[%2d]/ns2:series[%3d]/ns2:variable/ns2:variableCode[%4d]/@vocabulary";

variableCode = holder.getNodeValue(String.format(resVariableCodeXpath,siteIndex,1,seriesIndex,1) );
log.info("variableCode " + variableCode);
variableVocabulary =  holder.getNodeValue( String.format(resVariableVocabularyXpath,siteIndex,1,seriesIndex,1) );
log.info("variableVocabulary " + variableVocabulary);

resBeginDateXpath = "//ns2:sitesResponse/ns2:site[%1d]/ns2:seriesCatalog[%2d]/ns2:series[%3d]/ns2:variableTimeInterval/ns2:beginDateTime" ;
beginDate = holder.getNodeValue( String.format(resBeginDateXpath, siteIndex,1,seriesIndex) ) ;
log.info("beginDate" + beginDate);
Calendar cal = Calendar.getInstance();
sdf = new java.text.SimpleDateFormat('yyyy-MM-dd');
def begin = sdf.parse(beginDate);
cal.set(begin.getYear()+1900,begin.getMonth(),begin.getDay());

cal.add(Calendar.DATE, 2);
log.info("Calendar Date" + sdf.format(cal.getTime()));

requestHolder = groovyUtils.getXmlHolder("GetSiteInfoObjectCall#Request");
requestHolder.declareNamespace('ns1','http://www.cuahsi.org/his/1.0/ws/');
requestHolder.declareNamespace('ns2','http://www.cuahsi.org/waterML/1.0/');

requestHolder.setNodeValue("//ns1:site", siteNetwork + ':' + siteCode);

requestHolder.updateProperty();

requestHolder2 = groovyUtils.getXmlHolder("GetValuesObject#Request");
requestHolder2.declareNamespace('ns1','http://www.cuahsi.org/his/1.0/ws/');
requestHolder2.declareNamespace('ns2','http://www.cuahsi.org/waterML/1.0/');

requestHolder2.setNodeValue("//ns1:location", siteNetwork + ':' + siteCode);
requestHolder2.setNodeValue("//ns1:variable", variableVocabulary + ':' + variableCode);

requestHolder2.setNodeValue("//ns1:startDate", beginDate);
requestHolder2.setNodeValue("//ns1:endDate", sdf.format(cal.getTime()));

requestHolder2.updateProperty();

